import React, { Component } from 'react';
import logo from '../logo.svg';
import '../App.css';

class Content extends Component {
  constructor(props){
   super(props);
   this.state = {
     lang: '',
     languages:[]
   }
   this.updateLang = this.updateLang.bind(this);
   this.handleChange = this.handleChange.bind(this);
  }

  updateLang(){
    this.props.updateLang(this.state.lang);
  }

  handleChange(event){
    this.setState({lang: event.target.value}, this.updateLang);
  }

  getLanguageData(){
    $.ajax({
      url:'http://localhost:3000/testFile.json',
      dataType:'json',
      cache: false,
      success: function(data){
       this.setState({languages: data.language});

      }.bind(this),
      error: function(xhr, status, err){
        console.log(err);
      }
    });
  }

  componentDidUpdate(prevProps, prevState) {
    this.setState( {lang: this.props.match.params.locale}, this.getLanguageData );
  }

  render(){


    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
          <select onChange={this.handleChange} value={lang}>
              {languagesSelect}
          </select>
        </header>
      </div>
    );
  };
}

export default Content;
